mapper.select:
SELECT dai.id id, dai.`code` CODE, dav.version appServiceVersion FROM devops_app_service_instance dai, devops_app_service da, devops_env de, devops_app_service_version dav WHERE dai.app_service_id = da.id AND dai.env_id = de.id AND dai.`status` = 'running' AND de.project_id = #{projectId} AND dai.app_service_version_id = dav.id
AND dai.env_id = #{envId}
AND dai.app_service_version_id = #{versionId}
AND dai.app_service_id = #{appServiceId}
ORDER BY dai.id DESC

SELECT dai.id id, dai.`code` CODE, dav.version appServiceVersion, dav.id appServiceVersionId FROM devops_app_service_instance dai, devops_app_service da, devops_env de, devops_app_service_version dav, devops_env_command dc WHERE dai.app_service_id = da.id AND dai.command_id = dc.id AND dai.env_id = de.id AND dai.`status` in ('running','failed') AND de.project_id = #{projectId} AND dc.object_version_id= dav.id
AND dai.env_id = #{envId}
AND dai.app_service_id = #{appServiceId}
ORDER BY dai.id DESC

SELECT
COUNT(1)
FROM
devops_app_service_instance dai
WHERE 1=1

    AND dai.env_id = #{envId}


    AND dai.app_service_id = #{appServiceId}


    AND dai.code = #{appServiceInstanceCode}

SELECT da.id appServiceId, dam.share_level publishLevel, da.`code` appServiceCode, da.`name` appServiceName, de.project_id projectId, dav.id versionId, dai.id instanceId, dai.status instanceStatus, dai.`code` instanceCode, dav.version version, dav.creation_date versionCreationDate, de.id envId FROM devops_app_service da JOIN devops_app_service_version dav ON dav.app_service_id = da.id JOIN devops_app_service_instance dai ON dai.app_service_version_id = dav.id AND dai.status != 'deleted' JOIN devops_env de ON de.id = dai.env_id LEFT JOIN devops_app_service_share_rule dam ON dam.app_service_id = da.id WHERE de.project_id = #{projectId}
AND da.id = #{appServiceId}
AND de.id in
#{envIds}
AND de.id is null
ORDER BY da.last_update_date DESC, dav.creation_date DESC, dai.last_update_date DESC;

SELECT detail.message
FROM devops_app_service_instance ins
INNER JOIN devops_env_resource re ON ins.id = re.instance_id
INNER JOIN devops_env_resource_detail detail ON re.resource_detail_id = detail.id
WHERE ins.id = #{instanceId}
AND re.kind = #{resourceType}
AND re.name = #{resourceName};
