mapper.select:
SELECT DISTINCT da.id, da.`code`, da.`name`, da.project_id project_id FROM devops_app_service da JOIN devops_app_service_instance dai ON dai.app_service_id = da.id JOIN devops_env de ON de.id = dai.env_id WHERE de.project_id = #{projectId} AND de.type = 'user'
AND dai.status = #{status}
AND de.id = #{envId}
ORDER BY da.id not in (#{appServiceId}), da.id ASC

SELECT das.*, (SELECT COUNT(1) = 0 FROM devops_branch db WHERE db.app_service_id = das.id) AS empty_repository FROM devops_app_service das WHERE das.is_active IS TRUE AND das.is_synchro IS TRUE AND das.gitlab_project_id IS NOT NULL AND das.project_id = #{projectId}
AND (das.code LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%') OR das.name LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%') )
order by das.external_config_id is null desc

SELECT das.id, das.name, das.code FROM devops_app_service das WHERE das.is_active IS TRUE AND das.is_synchro IS TRUE AND das.gitlab_project_id IS NOT NULL AND das.project_id = #{projectId}
AND (das.code LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%') OR das.name LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%') )
ORDER BY das.id=#{targetAppServiceId} DESC

SELECT DISTINCT da.*
FROM devops_app_service da
JOIN devops_app_service_instance dai ON dai.app_service_id = da.id
AND dai.`status` != 'deleted'
JOIN devops_env de ON dai.env_id = de.id
WHERE da.is_active
AND de.project_id = #{projectId}

    AND de.id=#{envId}


    AND  da.NAME = #{appServiceName,jdbcType=VARCHAR}

ORDER BY da.id DESC

mapper.update:
UPDATE devops_app_service da
set da.is_failed = null
where da.id = #{appServiceId}

UPDATE devops_app_service da


        set da.harbor_config_id = #{newConfigId}
        where (da.harbor_config_id = #{oldConfigId} or da.harbor_config_id is null)


        set da.harbor_config_id = #{newConfigId}
        where da.harbor_config_id = #{oldConfigId}


AND da.project_id = #{projectId}

UPDATE devops_app_service da
set da.harbor_config_id = null
where da.harbor_config_id = #{harborConfigId}

UPDATE devops_app_service da
set da.chart_config_id = null
where da.chart_config_id = #{chartConfigId}

UPDATE devops_app_service da
set da.harbor_config_id = null
where da.id = #{appServiceId}

UPDATE devops_app_service da
set da.chart_config_id = null
where da.id = #{appServiceId}

mapper.select:
(SELECT DISTINCT da.id, da.`code`, da.`name`, da.project_id, da.chart_config_id, da.harbor_config_id FROM devops_app_service_share_rule dasr JOIN devops_app_service da ON dasr.app_service_id = da.id WHERE dasr.share_level = 'organization' AND da.is_active = TRUE
) UNION ( SELECT DISTINCT da.id, da.`code`, da.`name`, da.project_id, da.chart_config_id, da.harbor_config_id FROM devops_app_service da LEFT JOIN (SELECT app_service_id, project_id FROM devops_app_service_share_rule WHERE share_level = 'project' ) dasr ON da.id = dasr.app_service_id WHERE dasr.app_service_id IS NOT NULL AND da.is_active = TRUE
AND dasr.project_id = #{projectId}
)

select *
from devops_app_service

    project_id = #{projectId}

select DISTINCT das.* from devops_app_service das JOIN devops_app_service_version dasv ON dasv.app_service_id = das.id where dasv.id is not null
AND
dasv.id = #{id}

mapper.update:
UPDATE devops_app_service
SET group_id = #{groupId},
    artifact_id = #{artifactId}
WHERE id = #{id}

mapper.select:
SELECT id, last_update_date
FROM devops_app_service
WHERE last_updated_by = #{userId}
AND last_update_date > #{time}
AND devops_app_service.project_id IN

    #{projectId}

SELECT das.project_id, COUNT(*) AS app_svc_num FROM devops_app_service das WHERE das.project_id IN
#{id}
GROUP BY das.project_id

SELECT * FROM devops_app_service das WHERE das.is_active IS TRUE AND das.is_synchro IS TRUE AND das.gitlab_project_id IS NOT NULL AND das.id IN
#{appServiceId}
AND (das.code LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%') OR das.name LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%') )

select das.id, das.project_id
from devops_app_service das
         inner join devops_app_service_version dasv on das.id = dasv.app_service_id
where dasv.version = #{version}
  and das.code = #{appServiceCode}
  and das.project_id in

    #{projectId}

SELECT da.id, da.`code`, da.`name`, da.project_id, da.chart_config_id, da.harbor_config_id FROM (SELECT distinct dasv.app_service_id FROM devops_app_service_version dasv INNER JOIN (SELECT dasr.id, dasr.app_service_id, dasr.version_type, dasr.version FROM devops_app_service_share_rule dasr INNER JOIN (SELECT das.id FROM devops_app_service das WHERE das.project_id IN (
#{projectId}
)
AND das.external_config_id IS NULL
AND das.is_active = true) dastmp ON dasr.app_service_id = dastmp.id WHERE dasr.share_level = 'organization' OR (dasr.share_level = 'project' AND dasr.project_id = #{currentProjectId})) rule ON dasv.app_service_id = rule.app_service_id WHERE dasv.version = rule.version OR (rule.version_type is not null AND dasv.version like CONCAT('%', CONCAT(rule.version_type, '%')))) ids INNER JOIN devops_app_service da ON ids.app_service_id = da.id
AND da.type = #{type}
AND
(da.code LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%') OR da.name LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%') )

select das.project_id, das.code as app_service_code, das.name as app_service_name from devops_app_service das
das.project_id in
#{projectId}
and das.code in
#{code}

SELECT * FROM ( SELECT any_value ( deas.app_service_id ) AS ID, any_value ( deas.source ) AS source, any_value ( deas.service_name ) AS serviceName, any_value ( deas.service_code ) AS serviceCode, any_value ( deas.env_id ) AS envId, any_value ( deas.id ) AS tempId FROM devops_env_app_service deas JOIN devops_env de ON deas.env_id = de.id WHERE de.project_id=#{projectId,jdbcType=BIGINT}
AND deas.env_id = #{envId,jdbcType=BIGINT}
AND deas.source= #{type}
AND ( deas.service_name LIKE CONCAT(CONCAT('%', #{params, jdbcType=VARCHAR}),'%') or deas.service_code LIKE CONCAT(CONCAT('%', #{params, jdbcType=VARCHAR}),'%') )
GROUP BY ID ) temp ORDER BY temp.tempId DESC

SELECT DISTINCT project_id FROM devops_app_service
WHERE id IN

    #{appId}
