mapper.select:
SELECT dav.id, dav.version, dav.creation_date FROM devops_app_service_version dav WHERE dav.app_service_id = #{appServiceId}
and dav.version LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%')
AND dav.repository IS NOT NULL ORDER BY dav.id DESC

SELECT dap.version, dap.id versionId, lv.app_service_id FROM devops_app_service_version dap JOIN ( SELECT MAX( dappv.id ) id, dappv.app_service_id FROM devops_app_service_version dappv GROUP BY dappv.app_service_id ) lv ON lv.id = dap.id JOIN devops_app_service da ON lv.app_service_id = da.id LEFT JOIN devops_app_service_share_rule dam on dam.app_service_id = da.id WHERE da.project_id = #{projectId} AND dap.repository IS NOT NULL OR ( da.project_id IN
#{project}
AND dam.share_level = 'organization' ) OR dam.share_level = 'public'

SELECT * FROM (
(SELECT ver.id, ver.version, ver.creation_date FROM devops_app_service_version ver WHERE ver.id = #{appServiceVersionId,jdbcType=BIGINT}) UNION ALL
( SELECT distinct dav.id, dav.version, dav.creation_date FROM devops_app_service_version dav
JOIN devops_app_service_share_rule dassr ON dassr.app_service_id = dav.app_service_id AND ( dassr.share_level = 'organization' OR dassr.project_id = #{projectId} ) AND ( dassr.version = dav.version OR dav.version LIKE CONCAT('%', dassr.version_type, '%') )
WHERE dav.app_service_id = #{appServiceId}
AND dav.id != #{appServiceVersionId,jdbcType=BIGINT}
AND dav.repository IS NOT NULL
AND dav.`commit` IS NOT NULL
AND dav.version = #{version}
)) tmp
ORDER BY tmp.id != ${appServiceVersionId}, tmp.id DESC
ORDER BY tmp.id DESC

SELECT dav.id, dav.version, dav.publish_time, dav.creation_date, dav.app_service_id FROM devops_app_service_version dav WHERE dav.app_service_id = #{appServiceId}
AND dav.id in
#{versionId}
AND dav.version = #{version}
ORDER BY dav.version desc

SELECT dav.id, dav.version, dav.creation_date, dav.app_service_id FROM devops_app_service_version dav WHERE
dav.app_service_id in
#{serviceId}
AND dav.id in
#{versionId}
AND dav.version = #{version}
ORDER BY dav.id DESC

SELECT * FROM devops_app_service_version dav where 1=1 AND dav.repository IS NOT NULL
AND dav.id in
#{appServiceServiceIds}

(SELECT version.* FROM devops_app_service_share_rule dassr LEFT JOIN devops_app_service_version version ON dassr.app_service_id = version.app_service_id WHERE dassr.app_service_id in
#{id}
and dassr.share_level = 'organization'
and dassr.share_level = 'project' and dassr.project_id = #{projectId}
AND dassr.version IS NOT NULL AND dassr.version = version.version
AND dassr.version LIKE CONCAT('%', CONCAT(#{params},'%')) ) UNION ( SELECT version.* FROM devops_app_service_share_rule dassr LEFT JOIN devops_app_service_version version ON dassr.app_service_id = version.app_service_id WHERE dassr.app_service_id in
#{id}
and dassr.share_level = 'organization'
and dassr.share_level = 'project' and dassr.project_id = #{projectId}
AND dassr.version IS NULL AND version.version LIKE CONCAT('%', CONCAT(dassr.version_type,'%'))
AND dassr.version LIKE CONCAT('%', CONCAT(#{params},'%'))
)
select * from devops_app_service_version WHERE app_service_id in
#{id}
AND version LIKE CONCAT('%', CONCAT(#{params},'%'))
order by id desc

select * from devops_app_service_version where app_service_id=#{appServiceId}
and version in(select version from devops_app_service_share_rule where app_service_id=#{appServiceId})
order by last_update_date desc

SELECT dasv.id, dasv.version, dasv.app_service_id, das. CODE AS appServiceCode, das.`name` AS appServiceName, das.type AS appServiceType FROM devops_app_service_version dasv JOIN devops_app_service das ON das.id = dasv.app_service_id
WHERE dasv.id in
#{versionId}
ORDER BY CASE dasv.id
WHEN #{versionId} THEN #{index}
END

mapper.delete:
DELETE FROM devops_app_service_version
where id in

    #{versionId}
