mapper.select:
SELECT dav.id, dav.version, dav.creation_date, dashv.value_id, dashv.harbor_config_id, dashv.helm_config_id, dashv.harbor_repo_type as repo_type, dashv.readme_value_id FROM devops_app_service_version dav left join devops_app_service_helm_version dashv on dav.id = dashv.app_service_version_id WHERE dav.app_service_id = #{appServiceId}
and dav.version LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%')
AND dashv.repository IS NOT NULL ORDER BY dav.id DESC

SELECT dav.id, dav.version, dav.app_service_id, dav.commit, dav.ref, dav.object_version_number, dav.created_by, dav.creation_date, dav.last_updated_by, dav.last_update_date, dashv.harbor_repo_type as repo_type, dashv.helm_config_id, dashv.harbor_config_id, dashv.value_id, dashv.readme_value_id, dashv.image, dashv.repository FROM devops_app_service_version dav left join devops_app_service_helm_version dashv on dashv.app_service_version_id = dav.id where 1=1 AND dashv.repository IS NOT NULL
AND dav.id in
#{appServiceServiceIds}

SELECT dav.version
FROM devops_gitlab_pipeline dgp
         LEFT JOIN devops_gitlab_commit dgc ON dgp.commit_id = dgc.id
         LEFT JOIN devops_app_service_version dav
                   ON dav.`commit` = dgc.commit_sha and dav.app_service_id = #{appServiceId}
WHERE dgp.pipeline_id = #{pipelineId}
  and dav.ref = #{branch}
limit 1

(SELECT version.* FROM devops_app_service_share_rule dassr LEFT JOIN devops_app_service_version version ON dassr.app_service_id = version.app_service_id WHERE dassr.app_service_id in
#{id}
and dassr.share_level = 'organization'
and dassr.share_level = 'project' and dassr.project_id = #{projectId}
AND dassr.version IS NOT NULL AND dassr.version = version.version
AND dassr.version LIKE CONCAT('%', CONCAT(#{params},'%')) ) UNION ( SELECT version.* FROM devops_app_service_share_rule dassr LEFT JOIN devops_app_service_version version ON dassr.app_service_id = version.app_service_id WHERE dassr.app_service_id in
#{id}
and dassr.share_level = 'organization'
and dassr.share_level = 'project' and dassr.project_id = #{projectId}
AND dassr.version IS NULL AND version.version LIKE CONCAT('%', CONCAT(dassr.version_type,'%'))
AND dassr.version LIKE CONCAT('%', CONCAT(#{params},'%'))
)
select * from devops_app_service_version WHERE app_service_id in
#{id}
AND version LIKE CONCAT('%', CONCAT(#{params},'%'))
order by id desc

select * from devops_app_service_version where app_service_id=#{appServiceId}
and version in(select version from devops_app_service_share_rule where app_service_id=#{appServiceId})
order by last_update_date desc

SELECT dasv.id, dasv.version, dasv.app_service_id, dashv.harbor_config_id, dashv.harbor_repo_type as repo_type, dashv.image, das. CODE AS appServiceCode, das.`name` AS appServiceName, das.type AS appServiceType, das.project_id AS projectId FROM devops_app_service_version dasv left join devops_app_service_helm_version dashv on dashv.app_service_version_id = dasv.id JOIN devops_app_service das ON das.id = dasv.app_service_id
WHERE dasv.id in
#{versionId}

mapper.delete:
DELETE FROM devops_app_service_version
where id in

    #{versionId}
