mapper.select:
SELECT dai.id id, dai.`code` CODE, dav.version appVersion FROM devops_app_instance dai, devops_application da, devops_env de, devops_app_version dav WHERE dai.app_id = da.id AND dai.env_id = de.id AND dai.`status` = 'running' AND de.project_id = #{projectId} AND dai.app_version_id = dav.id
AND dai.env_id = #{envId}
AND dai.app_version_id = #{versionId}
AND dai.app_id = #{appId}
ORDER BY dai.id DESC

SELECT dai.id id, dai.`code` CODE, dav.version appVersion FROM devops_app_instance dai, devops_application da, devops_env de, devops_app_version dav WHERE dai.app_id = da.id AND dai.env_id = de.id AND dai.`status` in ('running','failed') AND de.project_id = #{projectId} AND dai.app_version_id = dav.id
AND dai.env_id = #{envId}
AND dai.app_id = #{appId}
ORDER BY dai.id DESC

SELECT
COUNT(1)
FROM
devops_app_instance dai
WHERE 1=1

    AND dai.env_id = #{envId}


    AND dai.app_id = #{appId}


    AND dai.id = #{appInstanceId}

SELECT da.id appId, dam.publish_level publishLevel, da.`code` appCode, da.`name` appName, da.project_id projectId, dav.id versionId, dai.id instanceId, dai.status instanceStatus, dai.`code` instanceCode, dav.version version, dav.creation_date versionCreationDate, de.id envId FROM devops_application da JOIN devops_app_version dav ON dav.app_id = da.id JOIN devops_app_instance dai ON dai.app_version_id = dav.id AND dai.status != 'deleted' JOIN devops_env de ON de.id = dai.env_id LEFT JOIN devops_app_market dam ON dam.app_id = da.id WHERE de.project_id = #{projectId}
AND da.id = #{appId}
AND de.id in
#{envIds}
AND de.id is null
ORDER BY da.last_update_date DESC, de.sequence ASC, dav.creation_date DESC, dai.last_update_date DESC;
