mapper.select:
SELECT dai.id id, dai.`code` CODE FROM devops_app_instance dai, devops_application da WHERE dai.app_id = da.id AND dai.`status` = 'running' AND da.project_id = #{projectId}
AND dai.env_id = #{envId}
AND dai.app_version_id = #{versionId}
AND dai.app_id = #{appId}
ORDER BY dai.id DESC

SELECT
COUNT(1)
FROM
devops_app_instance dai
WHERE
1 = 1
AND dai.app_id = #{appId}
AND dai.env_id = #{envId}
AND dai.status = 'running'

    AND dai.app_version_id = #{appVersionId}


    AND dai.id = #{appInstanceId}

SELECT da.id appId, dam.publish_level publishLevel, da.`code` appCode, da.`name` appName, dav.id versionId, dai.id instanceId, dai.status instanceStatus, dai.`code` instanceCode, dav.version version, dav.creation_date versionCreationDate, de.id envId FROM devops_application da JOIN devops_app_version dav ON dav.app_id = da.id JOIN devops_app_instance dai ON dai.app_version_id = dav.id AND dai.status != 'deleted' JOIN devops_env de ON de.id = dai.env_id LEFT JOIN devops_app_market dam ON dam.app_id = da.id WHERE de.project_id = #{projectId}
AND da.id = #{appId}
ORDER BY da.last_update_date DESC, de.sequence ASC, dav.creation_date DESC, dai.last_update_date DESC;
