mapper.select:
SELECT dav.id, dav.version, dav.commit, da.code appCode, da.name appName, da.is_active appStatus, da.id appId, dav.creation_date FROM devops_application da RIGHT JOIN devops_app_version dav ON da.id = dav.app_id WHERE da.project_id = #{projectId}
AND da.id= #{appId}
AND (da.is_skip_check_permission = 1 OR ( da.is_skip_check_permission = 0 AND ( da.id IN ( SELECT daur.app_id FROM devops_app_user_rel daur WHERE daur.iam_user_id = #{userId} ))))
AND
dav.version LIKE CONCAT(CONCAT('%', #{version, jdbcType=VARCHAR}),'%')
AND dav.version LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%')

SELECT dap.version, dap.id versionId, lv.app_id FROM devops_app_version dap JOIN ( SELECT MAX( dappv.id ) id, dappv.app_id FROM devops_app_version dappv GROUP BY dappv.app_id ) lv ON lv.id = dap.id JOIN devops_application da ON lv.app_id = da.id LEFT JOIN devops_app_market dam on dam.app_id = da.id WHERE da.project_id = #{projectId} OR ( da.project_id IN
#{project}
AND dam.publish_level = 'organization' ) OR dam.publish_level = 'public'

SELECT * FROM devops_app_version dav where 1=1
AND dav.id in
#{appVersionIds}
