mapper.select:
SELECT dcp.id,dcp.`name`,dcp.is_enabled AS enabled, dcp.app_service_id,dcp.project_id, das.`name` AS app_service_name, das.gitlab_project_id, dcp.image,dcp.last_update_date, dcp.last_updated_by,dcp.creation_date, dcpr.status as latest_execute_status, dcpr.creation_date as latest_execute_date, dcpr.id is not null as has_record FROM devops_cicd_pipeline dcp INNER JOIN devops_app_service das ON dcp.app_service_id = das.id left join devops_ci_pipeline_record dcpr on dcpr.id = (SELECT max(id) FROM devops_ci_pipeline_record WHERE ci_pipeline_id = dcp.id)
dcp.project_id = #{projectId}
and (das.name like CONCAT(CONCAT('%', #{searchParam}),'%') or das.code like CONCAT(CONCAT('%', #{searchParam}),'%') or dcp.name like CONCAT(CONCAT('%', #{searchParam}),'%') )
and dcp.app_service_id in
#{appServiceId}
and dcp.is_enabled = #{enableFlag}
and dcpr.status = #{status}
ORDER BY dcp.id DESC
