mapper.select:
SELECT dadr.id, dadr.task_name, dadr.last_update_date, dadr.app_id, dadr.env_id, dadr.status, dai.status AS instanceStatus, da. NAME AS appName, dav.version, de. NAME AS envName, dai. CODE AS instanceName FROM devops_auto_deploy_record dadr JOIN devops_application da ON dadr.app_id = da.id JOIN devops_app_version dav ON dadr.version_id = dav.id JOIN devops_env de ON dadr.env_id = de.id
JOIN devops_env_user_permission deup ON ( deup.iam_user_id = #{userId} AND deup.is_permitted = 1 AND dadr.env_id = deup.env_id )
LEFT JOIN devops_app_instance dai ON dadr.instance_id = dai.id where dadr.project_id = #{projectId}
AND dadr.app_id = #{appId}
AND dadr.env_id = #{envId}
AND dadr.task_name = #{taskName}

sql.if:
AND ( dav.version LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%')
OR dai.code LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%')
)

mapper.update:
UPDATE devops_auto_deploy_record dadr
SET dadr. STATUS = #{status}
WHERE dadr.auto_deploy_id = #{autoDeployId}
