mapper.select:
SELECT db.*
FROM devops_branch db
WHERE db.app_service_id = #{appServiceId}
  and db.branch_name = #{branchName}
ORDER BY db.creation_date DESC
LIMIT 1

mapper.update:
update
        devops_branch
set issue_id=#{devopsBranchDTO.issueId}
where id = #{devopsBranchDTO.id}

mapper.select:
SELECT db.app_service_id AS id, max(db.last_update_date) AS last_update_date FROM devops_branch db JOIN devops_app_service das ON db.app_service_id = das.id WHERE db.last_updated_by = #{userId} AND db.last_update_date > #{time} AND das.project_id IN
#{projectId}
GROUP BY db.app_service_id, db.last_update_date

SELECT db.id,
       dsr.issue_id AS issue_id
FROM devops_branch db
          LEFT JOIN devops_issue_rel dsr ON dsr.object_id = db.id
WHERE db.app_service_id = #{appServiceId}
  AND db.branch_name = #{branchName}

SELECT db.id,
dsr.issue_id AS issue_id
FROM devops_branch db
LEFT JOIN devops_issue_rel dsr ON dsr.object_id = db.id
WHERE db.app_service_id = #{appServiceId}
AND db.id = #{branchId}
