mapper.update:
UPDATE devops_certification dc
SET dc.status=#{status}
WHERE dc.id = #{certId}

UPDATE devops_certification dc
SET dc.status= #{status}
WHERE dc.id = #{certId}
AND dc.status = 'operating'

mapper.select:
SELECT COUNT(*) = 0
FROM devops_certification
WHERE env_id = #{envId}
AND name = #{certName}

    AND id != #{certId}
