mapper.select:
select dcj.*, dcs.name as stage_name
from devops_ci_job dcj
         left join devops_ci_stage dcs on dcs.id = dcj.ci_stage_id
where dcj.ci_pipeline_id = #{ciPipelineId}
  and dcj.type = 'custom';

