mapper.select:
SELECT dcp.id,dcp.`name`,dcp.is_enabled AS enabled, dcp.app_service_id,dcp.trigger_type,dcp.project_id, das.`name` AS app_service_name, das.gitlab_project_id, dcp.image FROM devops_cicd_pipeline dcp INNER JOIN devops_app_service das ON dcp.app_service_id = das.id
dcp.project_id = #{projectId}
and das.name like CONCAT(CONCAT('%', #{name}),'%')
and dcp.app_service_id in
#{appServiceId}
ORDER BY dcp.id DESC

SELECT * FROM ( SELECT dcp.id,dcp.`name`,dcp.is_enabled AS enabled, dcp.app_service_id,dcp.trigger_type,dcp.project_id, das.`name` AS app_service_name, das.gitlab_project_id, dcp.image FROM devops_cicd_pipeline dcp INNER JOIN devops_app_service das ON dcp.app_service_id = das.id
dcp.project_id = #{projectId}
and das.name like CONCAT(CONCAT('%', #{name}),'%')
AND das.is_skip_check_permission = TRUE
UNION ALL
SELECT dcp.id,dcp.`name`,dcp.is_enabled AS enabled, dcp.app_service_id,dcp.trigger_type,dcp.project_id, das.`name` AS app_service_name, das.gitlab_project_id, dcp.image FROM devops_cicd_pipeline dcp INNER JOIN devops_app_service das ON dcp.app_service_id = das.id INNER JOIN devops_app_service_user_rel dasur ON dasur.app_service_id = das.id AND dasur.iam_user_id = #{memberId,jdbcType=BIGINT}
dcp.project_id = #{projectId}
and das.name like CONCAT(CONCAT('%', #{name}),'%')
AND das.is_skip_check_permission = FALSE
) all_ci ORDER BY all_ci.id DESC

SELECT dcp.id, dcp.name, dcp.project_id, dcp.app_service_id, dcp.trigger_type, dcp.object_version_number, dcp.created_by, dcp.creation_date, dcp.last_updated_by, dcp.last_update_date, dcp.is_enabled as enabled, dcp.token, dcp.image
FROM devops_cicd_pipeline dcp
WHERE dcp.token = #{token,jdbcType=VARCHAR}
