mapper.select:
SELECT id, name, code, project_id, object_id, env_id, operation_type, rdupm_type, chart_source as source, object_version_number, created_by, creation_date, last_updated_by, last_update_date FROM devops_deploy_app_center_env WHERE project_id = #{projectId, jdbcType=BIGINT}
AND env_id = #{envId, jdbcType=BIGINT}
AND name LIKE CONCAT(CONCAT('%', #{name, jdbcType=VARCHAR}),'%')
AND rdupm_type = #{rdupmType, jdbcType=VARCHAR}
ORDER BY id DESC
