mapper.select:
SELECT dpv.id, dpv.name, dpv.value, dpv.description, dpv.created_by, dpv.last_update_date, de.`name` AS env_name, da.`name` AS app_service_name, da.code AS app_service_code, dpv.env_id, dpv.app_service_id FROM devops_deploy_value dpv JOIN devops_env de ON de.id = dpv.env_id JOIN devops_app_service da ON da.id = dpv.app_service_id WHERE dpv.project_id = #{projectId}
AND dpv.app_service_id = #{appServiceId}
AND dpv.env_id = #{envId}

SELECT * FROM( SELECT dpv.id, dpv.name, dpv.value, dpv.description, dpv.created_by, dpv.last_update_date, de.`name` AS env_name, da.`name` AS app_service_name, da.code AS app_service_code, dpv.env_id, dpv.app_service_id FROM devops_deploy_value dpv JOIN devops_env de ON de.id = dpv.env_id JOIN devops_app_service da ON da.id = dpv.app_service_id WHERE dpv.project_id = #{projectId} AND de.is_skip_check_permission=true
AND dpv.app_service_id = #{appServiceId}
AND dpv.env_id = #{envId}
UNION
SELECT dpv.id, dpv.name, dpv.value, dpv.description, dpv.created_by, dpv.last_update_date, de.`name` AS env_name, da.`name` AS app_service_name, da.`code` AS app_service_code, dpv.env_id, dpv.app_service_id FROM devops_deploy_value dpv JOIN devops_env de ON de.id = dpv.env_id JOIN devops_app_service da ON da.id = dpv.app_service_id
JOIN devops_env_user_permission deup ON ( deup.iam_user_id = #{userId} AND deup.is_permitted = 1 AND dpv.env_id = deup.env_id )
WHERE dpv.project_id = #{projectId} AND de.is_skip_check_permission=false
AND dpv.app_service_id = #{appServiceId}
AND dpv.env_id = #{envId}
) c

SELECT dpv.id, dpv.name, dpv.value, dpv.description, dpv.created_by, dpv.last_update_date, de.`name` AS envName, da.`name` AS appServiceName, dpv.env_id, dpv.app_service_id FROM devops_deploy_value dpv JOIN devops_env de ON de.id = dpv.env_id JOIN devops_app_service da ON da.id = dpv.app_service_id
JOIN devops_env_user_permission deup ON ( deup.iam_user_id = #{userId} AND deup.is_permitted = 1 AND dpv.env_id = deup.env_id )
WHERE dpv.project_id = #{projectId}
AND dpv.app_service_id = #{appServiceId}
AND dpv.env_id = #{envId}
order by dpv.last_update_date desc
