mapper.update:
UPDATE devops_env_group deg
RIGHT JOIN (
SELECT
    id group_id
FROM
    devops_env_group,
    ( SELECT @rowno := 0 ) r
WHERE
    project_id = #{projectId}
ORDER BY
    FIELD( id,

            #{envGroupId}

     )
    ) t ON deg.id = t.group_id
    SET sequence = @rowno := @rowno + 1;
