mapper.select:
SELECT dp.id, dp.ip, da.project_id projectId, dp.`name`, dp.instance_id, dp.namespace namespace, dp.is_ready, dp.`status`, dp.creation_date, da.`name` appServiceName, dav.version appServiceVersion, dai.`code` instanceCode, de.id envId, de.`code` envCode, de.`name` envName, dp.object_version_number FROM devops_env_pod dp LEFT JOIN devops_app_service_instance dai ON dp.instance_id = dai.id LEFT JOIN devops_app_service_version dav ON dai.app_service_version_id = dav.id LEFT JOIN devops_app_service da ON dav.app_service_id = da.id LEFT JOIN devops_env de ON de.id = dai.env_id WHERE de.project_id = #{projectId} AND de.type = 'user'
AND ((dai.source = 'normal' AND da.id= #{appServiceId}) OR (dai.source = 'market' AND dai.app_service_id = #{appServiceId}) OR (dai.source = 'middleware' AND dai.app_service_id = #{appServiceId}) )
AND dai.id= #{instanceId}
AND de.id= #{envId}

SELECT COUNT(1)
FROM devops_env_pod dep
WHERE 1 = 1

    AND dep.instance_id = #{instanceId,jdbcType=BIGINT}


    AND dep.namespace = #{namespace,jdbcType=VARCHAR}


    AND dep.is_ready = #{is_ready,jdbcType=TINYINT}


    AND dep.status = #{status,jdbcType=VARCHAR}

SELECT dp.id, dp.ip, dp.`name`, dp.instance_id, dp.namespace namespace, dp.is_ready, dp.`status`, dp.creation_date, de.id envId, de.`code` envCode, de.`name` envName, dp.object_version_number FROM devops_env_pod dp LEFT JOIN devops_env de ON de.id = dp.env_id WHERE de.id = #{envId} and dp.owner_ref_kind = #{kind} and dp.owner_ref_name = #{name}
AND dp.`name` LIKE CONCAT(CONCAT('%', #{searchParam.name, jdbcType=VARCHAR}),'%')
AND dp.ip LIKE CONCAT(CONCAT('%', #{searchParam.ip, jdbcType=VARCHAR}),'%')
AND dp.is_ready = #{searchParam.ready}
AND
(dp.`name` LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%') OR dp.ip LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%') OR dp.is_ready LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%') )

SELECT *
FROM devops_env_pod
WHERE owner_ref_kind = #{ownerKind}
  AND owner_ref_name = #{ownerName}
