mapper.select:
SELECT dgc.id, dgc.user_id, dgc.app_service_id, dgc.ref, dgc.commit_date, dgc.commit_sha, dgc.commit_content, dgc.url, da.`name` appServiceName FROM devops_gitlab_commit dgc JOIN devops_app_service da ON dgc.app_service_id = da.id WHERE da.app_id = #{projectId}
AND dgc.app_service_id IN
#{appServiceId}
1
AND dgc.commit_date >= CONCAT('','${startDate}',' 00:00:00')
AND dgc.commit_date <= CONCAT('','${endDate}',' 23:59:59')
AND da.is_active = 1 AND dgc.app_service_id = da.id AND da.project_id = #{projectId}
AND dgc.commit_date >= CONCAT('','${startDate}',' 00:00:00')
AND dgc.commit_date <= CONCAT('','${endDate}',' 23:59:59')
ORDER BY dgc.commit_date DESC
