mapper.select:
SELECT dgc.app_service_id AS id, max(dgc.commit_date) AS last_update_date
FROM devops_gitlab_commit dgc
             JOIN devops_app_service das ON dgc.app_service_id = das.id
        WHERE dgc.user_id = #{userId}
          AND dgc.commit_date > #{time}
          AND das.project_id IN

    #{projectId}

GROUP BY dgc.app_service_id, dgc.commit_date

SELECT dgc.creation_date
FROM devops_gitlab_commit dgc
             JOIN devops_app_service das ON dgc.app_service_id = das.id
WHERE das.project_id = #{projectId}
  AND dgc.commit_date >= #{startDate}
  AND dgc.commit_date <= #{endDate}

SELECT dgc.id, dgc.user_id, dgc.app_service_id, dgc.ref, dgc.commit_date, dgc.commit_sha, dgc.commit_content, dgc.url, da.`name` appServiceName FROM devops_gitlab_commit dgc JOIN devops_app_service da ON dgc.app_service_id = da.id WHERE da.is_active = 1 AND dgc.user_id=#{userId} AND dgc.commit_date >= #{date} AND da.project_id IN
#{projectId}
ORDER BY dgc.commit_date DESC
