mapper.select:
SELECT any_value(dhair.app_id) AS ID, any_value(dhair.app_source) AS source
FROM `devops_host_app_instance_rel` dhair WHERE dhair.project_id = #{projectId,jdbcType=BIGINT}
AND dhair.app_source= #{type,jdbcType=VARCHAR}
AND dhair.host_id = #{hostId,jdbcType=BIGINT}
GROUP BY dhair.app_id

select * from( SELECT dhair.instance_id, dhair.app_source, dhair.instance_type, dhair.created_by, ddi.name as instance_name, ddi.status, dhair.app_id FROM `devops_host_app_instance_rel` dhair JOIN devops_docker_instance ddi ON ddi.id=dhair.instance_id
WHERE dhair.project_id = #{projectId,jdbcType=BIGINT}
AND dhair.host_id = #{hostId,jdbcType=BIGINT}
AND dhair.instance_type='docker_process'
UNION
SELECT dhair.instance_id, dhair.app_source, dhair.instance_type, dhair.created_by, dni.name as instance_name, dni.status, dhair.app_id FROM `devops_host_app_instance_rel` dhair JOIN devops_normal_instance dni ON dni.id=dhair.instance_id
WHERE dhair.project_id = #{projectId,jdbcType=BIGINT}
AND dhair.host_id = #{hostId,jdbcType=BIGINT}
AND dhair.instance_type='normal_process'
) temp where 1=1
AND instance_name LIKE CONCAT(CONCAT('%', #{name, jdbcType=VARCHAR}),'%')
AND instance_type = #{type,jdbcType=VARCHAR}
AND status = #{status,jdbcType=VARCHAR}
( instance_name LIKE CONCAT(CONCAT('%', #{params, jdbcType=VARCHAR}),'%') OR instance_type = #{params,jdbcType=VARCHAR} OR status =#{params,jdbcType=VARCHAR} )

SELECT * FROM ( SELECT ddi.id, ddi.created_by, ddi.NAME AS instance_name, ddi.STATUS, "docker_process" AS instance_type, ddi.host_id FROM devops_docker_instance ddi UNION SELECT dni.id, dni.created_by, dni.NAME AS instance_name, dni.STATUS, dni.instance_type, dni.host_id FROM devops_normal_instance dni ) temp WHERE host_id = #{hostId,jdbcType=BIGINT}
AND instance_name LIKE CONCAT(CONCAT('%', #{name, jdbcType=VARCHAR}),'%')
AND instance_type = #{type,jdbcType=VARCHAR}
AND status = #{status,jdbcType=VARCHAR}
( instance_name LIKE CONCAT(CONCAT('%', #{params, jdbcType=VARCHAR}),'%') OR instance_type = #{params,jdbcType=VARCHAR} OR status =#{params,jdbcType=VARCHAR} )
