mapper.select:
SELECT tmp.* FROM ( SELECT dha.id, dha.project_id, dha.host_id, dha.name, dha.code, dha.operation_type, dha.rdupm_type, dha.object_version_number, dha.created_by, dha.creation_date, dha.last_updated_by, dha.last_update_date, dh.NAME AS host_name, dhai.app_id, dhai.source_type, dhai.source_config, dhai.pre_command, dhai.run_command, dhai.post_command, dhai.kill_command, dhai.health_prob, dhai.ready, dhai.group_id, dhai.artifact_id, dhai.version, dhai.id AS instanceId, null AS status FROM devops_host_app dha LEFT JOIN devops_host dh ON dh.id = dha.host_id LEFT JOIN devops_host_app_instance dhai ON dhai.app_id = dha.id
dha.project_id = #{projectId} AND rdupm_type != 'docker'
and dha.host_id = #{hostId}
and dha.operation_type = #{operationType}
and dha.rdupm_type = #{rdupmType}
and dha.name LIKE CONCAT(CONCAT('%', #{params, jdbcType=VARCHAR}),'%')
UNION SELECT dha.*, dh.NAME AS host_name, ddi.app_id, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, ddi.id AS instanceId, ddi.status AS status FROM devops_host_app dha LEFT JOIN devops_host dh ON dh.id = dha.host_id LEFT JOIN devops_docker_instance ddi ON ddi.app_id = dha.id
dha.project_id = #{projectId} AND rdupm_type = 'docker' AND ddi.id IN (SELECT MAX( ddix.id ) FROM devops_docker_instance ddix WHERE ddix.app_id = dha.id)
and dha.host_id = #{hostId}
and dha.operation_type = #{operationType}
and dha.rdupm_type = #{rdupmType}
and dha.name LIKE CONCAT(CONCAT('%', #{params, jdbcType=VARCHAR}),'%')
) tmp ORDER BY tmp.id DESC

SELECT tmp.* FROM (
select dha.*, dh.name as host_name, dhai.app_id, dhai.source_type, dhai.source_config, dhai.pre_command, dhai.run_command, dhai.post_command, dhai.kill_command, dhai.health_prob, dhai.ready, dhai.group_id, dhai.artifact_id, dhai.version, dhai.id as instanceId, null as status from devops_host_app dha left join devops_host dh ON dh.id = dha.host_id left join devops_host_user_permission dhup ON dhup.host_id = dh.id left join devops_host_app_instance dhai on dhai.app_id = dha.id
dha.project_id = #{projectId} AND dhup.iam_user_id = #{userId} AND rdupm_type != 'docker'
and dha.host_id = #{hostId}
and dha.operation_type = #{operationType}
and dha.rdupm_type = #{rdupmType}
and dha.name LIKE CONCAT(CONCAT('%', #{params, jdbcType=VARCHAR}),'%')
UNION select dha.*, dh.name as host_name, ddi.app_id, ddi.source_type, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, ddi.status AS status from devops_host_app dha left join devops_host dh ON dh.id = dha.host_id left join devops_host_user_permission dhup ON dhup.host_id = dh.id LEFT JOIN devops_docker_instance ddi ON ddi.app_id = dha.id
dha.project_id = #{projectId} AND dhup.iam_user_id = #{userId} AND rdupm_type = 'docker' AND ddi.id IN (SELECT MAX( ddix.id ) FROM devops_docker_instance ddix WHERE ddix.app_id = dha.id)
and dha.host_id = #{hostId}
and dha.operation_type = #{operationType}
and dha.rdupm_type = #{rdupmType}
and dha.name LIKE CONCAT(CONCAT('%', #{params, jdbcType=VARCHAR}),'%')
) tmp order by tmp.id desc

SELECT count(*) = 0
FROM devops_host_app dha
WHERE dha.project_id = #{projectId}
AND dha.name = #{name}

    AND dha.id != #{appId}

SELECT count(*) = 0
FROM devops_host_app dha
WHERE dha.project_id = #{projectId}
AND dha.code = #{code, jdbcType=VARCHAR}

    AND dha.id != #{appId}

SELECT
dha.*,
dh.NAME AS host_name
FROM
devops_host_app dha
LEFT JOIN devops_host dh ON dh.id = dha.host_id

    dha.project_id = #{projectId}

        and dha.host_id = #{hostId}


        and dha.operation_type = #{operationType}


        and dha.rdupm_type = #{rdupmType}


        and dha.name LIKE CONCAT(CONCAT('%', #{params, jdbcType=VARCHAR}),'%')



    order by dha.id desc


    order by dha.id = #{appId} desc, dha.id desc

select dha.*,
dh.name as host_name
from devops_host_app dha
left join devops_host dh ON dh.id = dha.host_id
left join devops_host_user_permission dhup ON dhup.host_id = dh.id

    dha.project_id = #{projectId} AND dhup.iam_user_id = #{userId}

        and dha.host_id = #{hostId}


        and dha.operation_type = #{operationType}


        and dha.rdupm_type = #{rdupmType}


        and dha.name LIKE CONCAT(CONCAT('%', #{params, jdbcType=VARCHAR}),'%')



    order by dha.id desc


    order by dha.id = #{appId} desc, dha.id desc
