mapper.select:
SELECT dh.* FROM devops_host dh
dh.project_id = #{projectId}
and (dh.name LIKE CONCAT(CONCAT('%', #{searchParam}), '%') OR dh.host_ip LIKE CONCAT(CONCAT('%', #{searchParam}), '%'))

SELECT dh.*
FROM devops_host dh
        WHERE project_id = #{projectId,jdbcType=BIGINT}
          AND host_ip IS NOT NULL
          AND id IN

    #{hostId,jdbcType=BIGINT}

SELECT dh.* FROM devops_host dh WHERE project_id IN
#{projectId,jdbcType=BIGINT}
order by id desc

(
        SELECT dh.*
        FROM devops_host dh
                     INNER JOIN devops_host_user_permission dhup ON dhup.host_id = dh.id

            dhup.iam_user_id = #{userId}

        AND dh.project_id = #{projectId}


        and (dh.name LIKE CONCAT(CONCAT('%', #{searchParam}), '%')
                OR dh.host_ip LIKE CONCAT(CONCAT('%', #{searchParam}), '%'))


)
        UNION
        (
        SELECT dh.*
        FROM devops_host dh

    dh.created_by = #{userId}

        AND dh.project_id = #{projectId}


        and (dh.name LIKE CONCAT(CONCAT('%', #{searchParam}), '%')
                OR dh.host_ip LIKE CONCAT(CONCAT('%', #{searchParam}), '%'))


)
