mapper.select:
SELECT DISTINCT di.id id, di.project_id projectId, di.env_id envId, di.`name` name, di.`domain` domain, di.status, di.is_usable isUsable, di.cert_id certId, de.`name` envName, cmd.`status` commandStatus, cmd.command_type commandType, cmd.error error, de.`code` namespace FROM devops_ingress di JOIN devops_ingress_path dip ON dip.ingress_id = di.id JOIN devops_env de on de.id = di.env_id LEFT JOIN devops_env_command cmd on di.command_id = cmd.id LEFT JOIN devops_service ds on ds.id = dip.service_id WHERE di.project_id = #{projectId}
AND de.id = #{envId}
AND ds.id = #{serviceId}
AND di.domain LIKE CONCAT(CONCAT('%', #{searchParam.domain, jdbcType=VARCHAR}),'%')
AND di.name LIKE CONCAT(CONCAT('%', #{searchParam.name, jdbcType=VARCHAR}),'%')
AND de.name LIKE CONCAT(CONCAT('%', #{searchParam.envName, jdbcType=VARCHAR}),'%')
AND de.code LIKE CONCAT(CONCAT('%', #{searchParam.envCode, jdbcType=VARCHAR}),'%')
AND dip.path LIKE CONCAT(CONCAT('%', #{searchParam.path, jdbcType=VARCHAR}),'%')
AND ds.name LIKE CONCAT(CONCAT('%', #{searchParam.serviceName, jdbcType=VARCHAR}),'%')
AND di.id = #{searchParam.ingressId}
AND di.env_id = #{searchParam.envId}
AND ds.id = #{searchParam.serviceId}
AND
(di.domain LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%') OR di.name LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%') OR de.name LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%') OR de.code LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%') OR dip.path LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%') OR ds.name LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%'))

mapper.update:
UPDATE devops_ingress
SET status=#{status}
WHERE env_id = #{envId}
  AND name = #{name}
