mapper.select:
SELECT
count(1) > 0 exist
FROM
devops_ingress di
INNER JOIN devops_ingress_path dip ON di.id = dip.ingress_id
WHERE
di.domain = #{domain}

    AND di.id != #{ingressId}

AND dip.path = #{path}
AND di.env_id = #{envId}

mapper.delete:
DELETE FROM devops_ingress_path
WHERE ingress_id IN

    #{ingressId}
