mapper.select:
SELECT dpv.id id, dpv.pvc_name pvc_name, dpv.project_id project_id, dpv.cluster_id cluster_id, dpv.name name, dpv.type type, dpv.description description, dpv.status status, dpv.request_resource request_resource, dpv.access_modes access_modes, dpv.command_id command_id, dc.name cluster_name, dpv.skip_check_project_permission skip_check_project_permission FROM devops_pv dpv LEFT JOIN devops_cluster dc on dpv.cluster_id = dc.id
dc.organization_id=#{organizationId}
AND dpv.project_id=#{projectId}
AND dpv.cluster_id=#{clusterId}
ORDER BY
${orderBy},
dpv.id DESC

SELECT dpv.*
FROM devops_pv dpv
LEFT JOIN devops_cluster dc ON dpv.cluster_id = dc.id
WHERE dc.system_env_id = #{envId}
AND dpv.name = #{pvName}

SELECT dpv.*, dc.system_env_id as env_id
FROM devops_pv dpv
LEFT JOIN devops_cluster dc ON dpv.cluster_id = dc.id
WHERE dpv.name = #{name}
AND dpv.cluster_id = #{clusterId}

mapper.update:
UPDATE devops_pv
SET devops_pv.status = #{status}
WHERE devops_pv.id = #{id}

mapper.select:
SELECT * FROM devops_pv
WHERE id IN

    #{pvId}
