mapper.select:
SELECT count(1) FROM ( SELECT DISTINCT ds.id FROM ( SELECT ds.* FROM devops_service ds INNER JOIN devops_env denv ON ds.env_id = denv.id WHERE denv.project_id = #{projectId} AND ds.`status` != 'deleted'
AND ds.env_id = #{envId}
) ds JOIN devops_env de ON ds.env_id = de.id LEFT JOIN devops_application da ON da.id = ds.app_id LEFT JOIN devops_service_app_instance dsai ON dsai.service_id = ds.id LEFT JOIN devops_app_instance dai ON dai.id = dsai.app_instance_id WHERE 1=1
) t

SELECT ds.id, ds.`name`, ds.external_ip, ds.ports, ds.`status`, ds.`type`, ds.labels, da.id AS app_id, da.`name` AS app_name, de.id AS env_id, de.`name` AS env_name, de.`code` AS namespace, da.project_id AS appProjectId, dsai.app_instance_id AS app_instance_id, dsai.`code` AS code, dai.`status` AS intance_status FROM ( SELECT devs.* FROM devops_service devs INNER JOIN devops_env denv ON devs.env_id = denv.id WHERE denv.project_id = #{projectId} AND devs.`status` != 'deleted'
AND devs.env_id = #{envId}
LIMIT #{start}, #{size}
) ds JOIN devops_env de ON ds.env_id = de.id LEFT JOIN devops_application da ON da.id = ds.app_id LEFT JOIN devops_service_app_instance dsai ON dsai.service_id = ds.id LEFT JOIN devops_app_instance dai ON dai.id = dsai.app_instance_id WHERE 1
