mapper.select:
select dp.id, dp.project_id, dp.name, dp.effect_version_id, dp.token, dp.is_enable as enable, dp.is_app_version_trigger_enable as app_version_trigger_enable, dp.object_version_number, dp.created_by, dp.creation_date, dp.last_updated_by, dp.last_update_date, dpr.status, dpr.started_date, dpr.finished_date, dpr.trigger_type, dpr.app_service_version_id, dpr.created_by from devops_pipeline dp left join devops_pipeline_record dpr on dpr.id = (select max(id) from devops_pipeline_record where pipeline_id = dp.id)
dp.project_id = #{projectId}
and dp.name like CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%')
and dp.is_enabled = #{enable}
and dp.trigger_type = #{triggerType}
and dpr.status = #{status}

select count(1) = 0
from devops_pipeline dp

    dp.project_id = #{projectId} and dp.name = #{name}

        and dp.id != #{id}
