mapper.select:
select dp.id, dp.project_id, dp.name, dp.effect_version_id, dp.token, dp.is_enable as enable, dp.is_app_version_trigger_enable as app_version_trigger_enable, dp.object_version_number, dp.created_by, dp.creation_date, dp.last_updated_by, dp.last_update_date, dpr.id as latest_pipeline_record_id, dpr.status, dpr.started_date, dpr.finished_date, dpr.trigger_type, dpr.app_service_version_id, dpr.created_by as latest_pipeline_trigger_id from devops_pipeline dp left join devops_pipeline_record dpr on dpr.id = (select max(id) from devops_pipeline_record where pipeline_id = dp.id)
dp.project_id = #{projectId}
and dp.name like CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%')
and dp.is_enable = #{enable}
and dpr.trigger_type = #{triggerType}
and dpr.status = #{status}
order by dp.id desc

select count(1) = 0
from devops_pipeline dp

    dp.project_id = #{projectId} and dp.name = #{name}

        and dp.id != #{id}

select dpj.name as job_name, dp.name as pipeline_name
from devops_pipeline_chart_deploy_cfg dpcdc
         inner join devops_pipeline_job dpj on dpcdc.id = dpj.config_id
         inner join devops_pipeline dp on dp.effect_version_id = dpj.version_id
where dp.project_id = #{projectId}
  and dpcdc.value_id = #{valueId}
