select.if:
SELECT DISTINCT dpr.id, dpr. STATUS, dpr.trigger_type, dpr.pipeline_name, dpr.last_update_date, dpr.project_id, dpr.pipeline_id, dpr.error_info FROM devops_pipeline_record dpr JOIN devops_pipeline_stage_record dpsr ON dpsr.pipeline_record_id = dpr.id AND dpsr.`status` = 'pendingcheck' JOIN devops_pipeline_task_record dptr ON dptr.stage_record_id = dpsr.id AND dptr.audit_user LIKE CONCAT(CONCAT('%', #{classifyParam.userId}), '%') AND NOT EXISTS ( SELECT 1 FROM devops_pipeline_user_record_rel dpurl WHERE dpurl.task_record_id = dptr.id AND dpurl.user_id = #{classifyParam.userId} ) WHERE dpr.project_id = #{projectId} AND dpr.`status` = 'pendingcheck'
AND dpr.pipeline_id = #{pipelineId}
UNION SELECT DISTINCT dpr.id, dpr. STATUS, dpr.trigger_type, dpr.pipeline_name, dpr.last_update_date, dpr.project_id, dpr.pipeline_id, dpr.error_info FROM devops_pipeline_record dpr JOIN devops_pipeline_stage_record dpsr ON dpsr.pipeline_record_id = dpr.id AND dpsr.id IN ( SELECT max(dpsr.id) FROM devops_pipeline_stage_record dpsr WHERE dpsr.pipeline_record_id IN ( SELECT dpr.id FROM devops_pipeline_record dpr WHERE dpr.project_id = #{projectId} AND dpr.`status` = 'pendingcheck' AND NOT EXISTS ( SELECT 1 FROM devops_pipeline_stage_record dpsr WHERE dpsr.pipeline_record_id = dpr.id AND dpsr. STATUS = 'pendingcheck' ) ) AND dpsr. STATUS = 'success' GROUP BY dpsr.pipeline_record_id ) AND dpsr.audit_user LIKE CONCAT(CONCAT('%', #{classifyParam.userId}), '%') WHERE dpr.project_id = #{projectId} AND dpr.`status` = 'pendingcheck'
AND dpr.pipeline_id = #{pipelineId}


    UNION



    AND dpr.pipeline_id = #{pipelineId}


AND(dpr.created_by = #{classifyParam.userId} AND dpr.trigger_type = 'manual')

mapper.select:
SELECT dpr.id, dpr.project_id, dpr.`status`, dpr.creation_date, GROUP_CONCAT( DISTINCT dpad.env_id ) env FROM devops_pipeline_record dpr LEFT JOIN devops_pipeline_stage dps ON dpr.id = dps.pipeline_id LEFT JOIN devops_pipeline_task dpt ON dpt.stage_id = dps.id LEFT JOIN devops_pipeline_app_service_deploy dpad ON dpad.id = dpt.app_service_deploy_id
WHERE dpr.id = #{pipelineRecordId}
GROUP BY dpr.id

